import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { editCommentThunk } from './store/comments.js';


const CommentComponent = ({ comment, handleDelete }) => {
  const dispatch = useDispatch();

  const [newCommentText, setNewCommentText] = useState('');

  const editComment = () => {
    const newCommentInfo = {
      user_name: 'Drew_T',
      body: newCommentText
    }

    dispatch(editCommentThunk(newCommentInfo))
  }

  return (
    <div key={comment.id} className='single-comment'>
      <div>{comment.user_name}</div>
      <div>{comment.id}</div>
      <button
        className='delete-Button'
        onClick={() => handleDelete(comment.id)}
      >
        Delete Me
      </button>
      <div>{comment.body}</div>
      <div>EDIT THE COMMENT HERE</div>
      <input type='text' value={newCommentText} onChange={(e) => setNewCommentText(e.target.value)}></input>
      <button onClick={editComment}>EDIT ME!</button>
    </div>
  )
}

export default CommentComponent;
