import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { getCommentsThunk, deleteCommentThunk, createCommentThunk } from './store/comments.js'
import CommentComponent from './Comment.js';
import './App.css';


const wrapperStyle = {
  display: 'flex',
  justifyContent: 'center',
  flexDirection: 'column'
}

const h1Style = {
  position: 'sticky',
  top: '0',
  backgroundColor: '#f5f5f5',
  padding: '10px',
  margin: '0',
  fontSize: '2em',
  fontWeight: 'bold',
  textAlign: 'center',
  border: '1px solid #ccc'
}

function App() {
  const dispatch = useDispatch()
  const comments = useSelector(state => state.comments)

  const [commentText, setCommentText] = useState('')

  const handleDelete = (id) => {
    dispatch(deleteCommentThunk(id))
  }

  useEffect(() => {
    dispatch(getCommentsThunk())
  }, [dispatch])



  const submitComment = (e) => {
    e.preventDefault()
    const comment = {
      'user_name': 'Drew_T',
      'body': commentText
    }
    dispatch(createCommentThunk(comment))
  }


  const commentsSection = Object.values(comments)?.map(comment => (
    <CommentComponent key={comment.id} comment={comment} handleDelete={handleDelete} />
  )).reverse()


  return (
    <div style={wrapperStyle}>
      <h1 style={h1Style}>Article</h1>
      <div style={wrapperStyle}>
        <div> Let's pretend this is an article on something we care deeply about.</div>
        <br></br>
        <div> It's a really good article...  I promise!</div>
        <br></br>
        <div> {`Let's all share our thoughts in the comments section.  =]`}</div>
      </div>
      <h3>Add your comment</h3>
      <form onSubmit={submitComment} >
        <label htmlFor='comment'>{`Add a comment here -------> > `}</label>
        <input type='textarea' onChange={(e) => setCommentText(e.target.value)} value={commentText}></input>
        <button>Add Comment</button>
      </form>
      <br></br>
      <div style={wrapperStyle}>
        <h1 style={h1Style}>COMMENTS</h1>
        <div className="App">
          {commentsSection}
        </div>
      </div>
    </div>
  );
}

export default App;
